// Sample product list
const products = [
    { id: 1, name: 'Shirt', price: 25.0, category: 'clothing', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSzz1PtJgceXntqvg5pYF8Krm7Z_tWSeuuK5Q&s' },
    { id: 2, name: 't-shirt', price: 89.99, category: 'clothing', image: 'data:image/jpeg;base64,' },
    { id: 3, name: 'Sunglasses', price: 19.99, category: 'accessories', image: 'data:image/jpeg;base64,' },
    { id: 4, name: 'Smartwatch', price: 129.0, category: 'gadgets', image: 'data:image/jpeg;base64,' },
    { id: 5, name: 'Jeans', price: 49.99, category: 'clothing', image: 'https://t3.ftcdn.net/jpg/04/83/25/50/360_F_483255019_m1r1ujM8EOkr8PamCHF85tQ0rHG3Fiqz.jpg' },
    { id: 6, name: 'Wallet', price: 29.99, category: 'accessories', image: 'data:image/jpeg;base64,' }
  ];
  
  let cart = [];
  
  // Elements
  const productGrid = document.getElementById('product-grid');
  const cartSidebar = document.getElementById('cart-sidebar');
  const cartToggle = document.getElementById('cart-toggle');
  const cartItems = document.getElementById('cart-items');
  const cartTotal = document.getElementById('cart-total');
  const cartCount = document.getElementById('cart-count');
  const darkToggle = document.getElementById('toggle-dark');
  
  // Render products
  function renderProducts(category = 'all') {
    productGrid.innerHTML = '';
    const filtered = category === 'all' ? products : products.filter(p => p.category === category);
  
    filtered.forEach(product => {
      const card = document.createElement('div');
      card.classList.add('product-card');
      card.innerHTML = `
        <img src="${product.image}" alt="${product.name}" />
        <h3>${product.name}</h3>
        <p>$${product.price.toFixed(2)}</p>
        <button onclick="addToCart(${product.id})">Add to Cart</button>
      `;
      productGrid.appendChild(card);
    });
  }
  
  // Add to cart
  function addToCart(productId) {
    const item = products.find(p => p.id === productId);
    const existing = cart.find(i => i.id === item.id);
  
    if (existing) {
      existing.quantity += 1;
    } else {
      cart.push({ ...item, quantity: 1 });
    }
    updateCart();
  }
  
  // Remove from cart
  function removeFromCart(productId) {
    cart = cart.filter(item => item.id !== productId);
    updateCart();
  }
  
  // Update cart display
  function updateCart() {
    cartItems.innerHTML = '';
    let total = 0;
    let count = 0;
  
    cart.forEach(item => {
      total += item.price * item.quantity;
      count += item.quantity;
  
      const li = document.createElement('li');
      li.innerHTML = `
        ${item.name} x${item.quantity} - $${(item.price * item.quantity).toFixed(2)}
        <button onclick="removeFromCart(${item.id})" style="margin-left:auto;">âœ–</button>
      `;
      cartItems.appendChild(li);
    });
  
    cartTotal.textContent = total.toFixed(2);
    cartCount.textContent = count;
  }
  
  // Toggle cart sidebar
  cartToggle.addEventListener('click', () => {
    cartSidebar.classList.toggle('open');
  });
  
  // Toggle dark mode
  darkToggle.addEventListener('click', () => {
    document.body.classList.toggle('dark');
  });
  
  // Filter products by category
  document.querySelectorAll('.filter-btn').forEach(btn => {
    btn.addEventListener('click', () => {
      const category = btn.getAttribute('data-category');
      renderProducts(category);
    });
  });
  
  // Initial render
  renderProducts();